var count = false;

function show_popap(arg) {

  var elem_from_dom = document.getElementsByClassName("common_block");

  var newelem = document.createElement('div');
  var new_span = document.createElement('span');
  new_span.innerHTML = arg;
  newelem.appendChild(new_span);

  newelem.classList.add("popap");

  count || elem_from_dom[0].appendChild(newelem);

  count = true;

  new_span.addEventListener("click", function() {
    elem_from_dom[0].removeChild(newelem);
    count = false;
  });

}

export default show_popap;
