import React from 'react';
import ReactDOM from 'react-dom';
import Lic from './lic_page';
import storage from '../storage';
import data from '../current_info_person';
//import http from '../http_request';


class Home extends React.Component {

  lic_page = () => {

   ReactDOM.render(<Lic arr_info_person={data}/>, document.getElementById('global'));
   var global = document.getElementById('global');
   global.style.background = "#ffffff";




  //   storage.setItem("login", "from staroge text");
  //  alert( storage.getItem("login"));

 // назначаем событие после индентификации элементов в DOM дереве в (lic_page)
  setTimeout(function(){

    var elem = document.getElementsByClassName("common_user");

    for(var i = 0;i < elem.length;i++){
      elem[i].addEventListener("click", function() {

       var elem = this;
       var arr = elem.childNodes;


       ReactDOM.render(<Home />, document.getElementById('global'));

       var lic_rah_abonent = document.getElementById("lic_rah_abonent");
       var name_abonent = document.getElementById("name_abonent");
       var addres_abonent = document.getElementById("addres_abonent");
       var kil_osib = document.getElementById("kil_osib");
  //     var type_rozrah = document.getElementById("type_rozrah");
  //     var service_one = document.getElementById("service_one");
  //     var service_two = document.getElementById("service_two");


       lic_rah_abonent.innerHTML = arr[0].innerHTML;
       name_abonent.innerHTML = arr[2].innerHTML;
       addres_abonent.innerHTML = arr[4].innerHTML;
       kil_osib.innerHTML = arr[6].innerHTML;
  //     type_rozrah.innerHTML = arr[8].innerHTML;
  //     service_one.innerHTML = arr[10].innerHTML;
  //     service_two.innerHTML = arr[12].innerHTML;


    });
   }

 }, 100);

  }

  balanse_page = () => {

//    console.log(storage.getItem("arr"));
//    alert("balanse");
  }

  setting_page = () => {

    alert("setting");

  }


  render() {

    return (
      <div id="home_page">
       <div id="header_info">
         <div id="lic_ruch">
            <span id="lic_rah_abonent">23350565621</span><br></br>
            <span id="name_abonent">Ковальчук Нина Александровна</span><br></br>
            <span id="addres_abonent">ул.Шекспира 1\8</span>
         </div>
         <hr/>

         <div>
           <span>Колличество чел.</span>
           <p id="kil_osib">1</p>
         </div>
         <div id="type_rozrah">
           <span>Тип расчета</span>
           <p>счетчик</p>
         </div>
         <hr/>

         <div id="service_one">
             <span>Водоподача</span>
             <p>12.09</p>
         </div>
         <div id="service_two">
             <span>Водоотвод</span>
             <p>23.09</p>
         </div>
         <hr/>

       </div>

      <div>
      <div id="position_button">
         <button id="style_button" onClick={this.lic_page}>Счета</button>
         <span id="text_under_button">списки счетов</span>
         </div>
         <div id="position_button">
          <button id="style_button" onClick={this.balanse_page}>Баланс</button>
          <span id="text_under_button">баланс</span>
          </div>
          <div id="position_button">
            <button id="style_button" onClick={this.setting_page}>Настройки</button>
             <span id="text_under_button">настройки</span>
            </div>
     </div>
     </div>
    );
  }

}

