import React from 'react';
import ReactDOM from 'react-dom';
var count = true;

class Setting extends React.Component {

 popap = (arg) => {



   var elem_from_dom = document.getElementsByClassName("common_block");

   var newelem = document.createElement('div');
   newelem.innerHTML = arg;

   newelem.classList.add("popap");

   if(count) {

   elem_from_dom[0].appendChild(newelem);

   count = false;
   }

 }

 info_version = () => {

    this.popap("info");

  //    popap();

 }



  render () {
    return (

       <div class="common_block">
        <div class="head_line_setting">
          <span>Настройки</span>
        </div>
        <div class="common_text">
          <div onClick={this.info_version}>
            <span>Информация о версии</span>
            <span>версия 2.0.9 </span>
          </div>
          <div>
            <span>Цветовая панель</span>
            <span>выбор основного цвета в проложении</span>
          </div>
        </div>

        </div>


    );
  }

}
