import React from 'react';
import ReactDOM from 'react-dom';
import Home from './home';
import data from '../current_info_person';
const text = "back";

class Balance extends React.Component {

  back = () => {

    ReactDOM.render(<Home />, document.getElementById('global'));

  }

  foo = () => {

    var info = this.props.info_person;

    var lic_rah = document.getElementById("lic_rah_abonent");

    var result_info_obj;

    var result = data.map(function(elem) {
      for(var key in elem) {
        if(+elem[key] == +lic_rah.innerHTML) {
          result_info_obj = isProm(elem);
        }
      }
    });

    return ( <div class="common_lich">
              <span>{result_info_obj.num}</span><br />
              <div class="content_left">
              <span class="one_line_name">Название счетчика</span>
              </div>
              <div class="content_right">
              <span class="one_line_value">{result_info_obj.name_lich}</span>
              </div>
              <hr/>
              <span class="one_line_name">Номер счетчика</span>
              <span class="one_line_value">{result_info_obj.num_lich}</span>
              <hr />



            </div> );


  }

  render () {

    return (
          <div>

             {this.foo()}

             <button onClick={this.back} id="button_back">{text}</button>

         </div>
    );

  }

}
function isProm(obj) {

   for(var key in obj) {
     if(key == "lich") {
       console.log(obj);
       return obj[key];
     }
   }

}
export default Balance;
