var count = false;

var colos = {

    show_popap: function(arg) {

      var elem_from_dom = document.getElementsByClassName("common_block");

      var new_elem_div = document.createElement('div');
      var p = document.createElement("p");
      var new_span = document.createElement('span');

      p.innerHTML = "pwmcpmwom ciwon cnqoiwnc ioqnw qoiwdn qoidnmdoq omq";
      new_span.innerHTML = arg;

      new_elem_div.appendChild(p);
      new_elem_div.appendChild(new_span);

      new_elem_div.classList.add("popap");

      count || elem_from_dom[0].appendChild(new_elem_div);

      count = true;

      new_span.addEventListener("click", function() {
        elem_from_dom[0].removeChild(new_elem_div);
        count = false;
      });

     }

}

export default colos;
